import React from 'react';
import { useNFTBalances, useMoralis } from 'react-moralis';
import { Card, Image, Tooltip, Skeleton } from 'antd';
import { FileSearchOutlined } from '@ant-design/icons';
import { getExplorer } from 'helpers/networks';
import { useTranslation } from 'react-i18next';
import { StyledNFTBalance } from './NFTBalanceScreen.styled';
const { Meta } = Card;

export const NFTBalanceScreen = () => {
  const { t } = useTranslation();

  const { data: NFTBalances } = useNFTBalances({
    // address: '0x9d919f040ee44c6e224395b3ea8a97f3ffc4a523',
  });
  const { chainId, isAuthenticated } = useMoralis();

  return (
    <StyledNFTBalance>
      <h1>ðŸ–¼ NFT Balances</h1>
      <div id="nfts-wrapper">
        <Skeleton loading={!NFTBalances?.result || !isAuthenticated}>
          {NFTBalances?.result.length > 0 ? (
            NFTBalances.result.map((nft, index) => {
              return (
                nft?.metadata?.image && (
                  <Card
                    id="nft"
                    hoverable
                    actions={[
                      <Tooltip title={t('nfts.view')}>
                        <FileSearchOutlined
                          onClick={() =>
                            window.open(
                              `${getExplorer(chainId)}address/${nft.token_address}`,
                              '_blank',
                            )
                          }
                        />
                      </Tooltip>,
                    ]}
                    cover={
                      <Image
                        preview={false}
                        src={nft?.image || 'error'}
                        fallback="data:image/png;base64,"
                        alt=""
                      />
                    }
                    key={index}
                  >
                    <Meta title={nft.metadata.name} description={nft.token_address} />
                  </Card>
                )
              );
            })
          ) : (
            <h2>{t('nfts.empty')}</h2>
          )}
        </Skeleton>
      </div>
    </StyledNFTBalance>
  );
};
